mapper.update:
UPDATE welfare
SET 			serv_id = #{servId}
				,serv_name = #{servName}
				,start_date = #{startDate}
				,end_date =#{endDate}
				,biz_dept = #{bizDept}
				,sido = #{sido}
				,sgg = #{sgg}
				,serv_summary =#{servSummary}
				,household = #{household}
				,like_subject = #{likeSubject}
				,sup_period =#{supPeriod}
				,prov_type = #{provType}
				,app_way = #{appWay}
				,sup_target = #{supTarget}
				,sel_standard =#{selStandard}
				,sal_serv = #{salServ}
WHERE wid= #{wid}

mapper.select:
SELECT u.user_no,w.wid,w.serv_id, u.Tel, u.sido, u.like_subject, u.household, w.serv_summary
FROM users u
JOIN welfare w 
on u.sido = #{vo.sido}
where u.LIKE_SUBJECT = #{vo.likeSubject}
and u.HOUSEHOLD = #{vo.household}
and u.receive = '수신'
and w.wid = #{vo.wid}

SELECT u.user_no,u.user_name,w.wid,w.serv_id, u.Tel, u.sido, u.like_subject, u.household, w.serv_summary
FROM users u
JOIN welfare w 
on u.sido = #{vo.sido}
where u.LIKE_SUBJECT = #{vo.likeSubject}
and u.HOUSEHOLD = #{vo.household}
and u.receive = '수신'
and w.wid = #{vo.wid}

select count(*)
from welfare w
join users u
on u.sido = #{vo.sido}
where u.LIKE_SUBJECT = #{vo.likeSubject}
and u.HOUSEHOLD = #{vo.household}
and u.receive = '수신'
and w.wid = #{wid}

