mapper.update:
UPDATE resume SET  resume_title = #{resumeTitle}
		, resume_name = #{resumeName}
		, birth = #{birth}
		, addr= #{addr}
		, tel = #{tel}
		, email= #{email}
		, produce= #{produce}
		, resume_img= #{resumeImg}
WHERE resume_no = #{resumeNo}

mapper.select:
SELECT DISTINCT user_no 
FROM users
WHERE user_id = #{userId}
